+      primaryRepoName={primaryRepoName} />,
+  ];
+
+  let reactionsStat = {
+    THUMBS_UP: 0,
+    THUMBS_DOWN: 0,
+    LAUGH: 0,
+    HOORAY: 0,
+    HEART: 0,
+    CONFUSED: 0
+  };
+  if (reactions) {
+    reactions.forEach(reaction => reactionsStat[reaction.content]++);
+  }
+
+  return (
+    <div className='-card-and-related'>
